function requestData(a){$.ajax({url:"api.app.php",cache:!1,dataType:"json",type:"GET",data:{request:"last7day",app_id:a},error:function(){console.log("Request Error")}}).done(function(a){var e=a.items,t=[],n=[];console.log(e),$.each(e,function(a,e){n.push(e.total),t.push(e.day)}),graphRender(n,t)})}function graphRender(a,e){{var t=document.getElementById("chart").getContext("2d");new Chart(t,{type:"bar",data:{labels:e,datasets:[{label:"Last 7 Days.",data:a,backgroundColor:"#4CAF50",borderWidth:1}]},options:{responsive:!0,title:{display:!1},legend:{display:!1},scales:{yAxes:[{ticks:{beginAtZero:!0}}]}}})}}$(document).ready(function(){var a=$("#app_id").val();requestData(a)});