function requestData(e){$.ajax({url:"api.app.php",cache:!1,dataType:"json",type:"POST",data:{action:"request_counter",app_id:e},error:function(){console.log("Request Error")}}).done(function(e){var a=e.data.items,t=[],r=[];$.each(a,function(e,a){t.push(a.request),r.push(a.create_time)}),graphRender(t.reverse(),r.reverse())})}function graphRender(e,a){{var t=document.getElementById("chart").getContext("2d");new Chart(t,{type:"bar",data:{labels:a,datasets:[{label:"Last 7 Days.",data:e,backgroundColor:"#4CAF50",borderWidth:1}]},options:{responsive:!0,title:{display:!1},legend:{display:!1},scales:{yAxes:[{ticks:{beginAtZero:!0}}]}}})}}$(document).ready(function(){var e=$("#app_id").val();requestData(e)});