function requestData(e){$.ajax({url:"api.queries.php",cache:!1,dataType:"json",type:"POST",data:{action:"request_counter",qid:e},error:function(){console.log("Request Error")}}).done(function(e){var t=e.data.items,a=[],r=[];$.each(t,function(e,t){a.push(t.request),r.push(t.create_time)}),graphRender(a.reverse(),r.reverse())})}function graphRender(e,t){{var a=document.getElementById("chart").getContext("2d");new Chart(a,{type:"bar",data:{labels:t,datasets:[{label:"Last 7 Days.",data:e,backgroundColor:"#4CAF50",borderWidth:1}]},options:{responsive:!0,title:{display:!1},legend:{display:!1},scales:{yAxes:[{ticks:{beginAtZero:!0}}]}}})}}$(document).ready(function(){var e=$("#qid").val();requestData(e)});